sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (g_form.getValue('u_dch_ap_sub_type')==''){
		g_form.setVisible('u_dch_ap_approver', false);
		g_form.setVisible('u_dch_ap_region', false);
		g_form.setVisible('u_dch_ap_tel_no', false);
		g_form.setVisible('u_dch_ap_advanced_amount',false);
		g_form.setVisible('u_dch_ap_vendor_id', false);
		g_form.setVisible('u_dch_ap_vendor_brand', false);
		g_form.setVisible('u_dch_ap_vendor_bank_account', false);
		g_form.setVisible('u_dch_ap_vendor_bank_name', false);

	}

	if (g_form.getValue('u_dch_ap_type')=='Personal Write-Off' && g_form.getValue('u_dch_ap_sub_type')!==''){
		//Set Visible
		g_form.setVisible('u_dch_ap_approver', true);
		g_form.setVisible('u_dch_ap_region', true);
		g_form.setVisible('u_dch_ap_tel_no', true);

		//Set Not Visible
		g_form.setVisible('u_dch_ap_advanced_amount',false);
		g_form.setVisible('u_dch_ap_vendor_id', false);
		g_form.setVisible('u_dch_ap_vendor_brand', false);
		g_form.setVisible('u_dch_ap_vendor_bank_account', false);
		g_form.setVisible('u_dch_ap_vendor_bank_name', false);

		if (g_form.getValue('u_dch_ap_sub_type')=='Cash Advanced'){
			g_form.setVisible('u_dch_ap_advanced_amount',true);
		}else{
			g_form.setVisible('u_dch_ap_advanced_amount',false);
		}

	}else if (g_form.getValue('u_dch_ap_type')=='Payment for Vendor' && g_form.getValue('u_dch_ap_sub_type')!==''){
		//Set Visible
		g_form.setVisible('u_dch_ap_approver', true);
		g_form.setVisible('u_dch_ap_region', true);
		g_form.setVisible('u_dch_ap_tel_no', true);
		g_form.setVisible('u_dch_ap_vendor_id', true);
		g_form.setVisible('u_dch_ap_vendor_brand', true);
		g_form.setVisible('u_dch_ap_vendor_bank_account', true);
		g_form.setVisible('u_dch_ap_vendor_bank_name', true);

		//Set Not Visible
		g_form.setVisible('u_dch_ap_advanced_amount',false);

	}

}
